import React from "react";
import Button from '../button'
import styles from './styles.module.scss';

const Cart = ({
    cartItems = [],
    removeFromCart,
    borrowBook
}) => {
    return (
        <div className={styles.cart}>
            <div className={styles.cart__title}>
                Cart
            </div>
            <div className={styles.cart__inner}>
                <div>
                    {cartItems.map(item => 
                        <div className={styles.cart__item} key={item.bookId}>
                            <img src={item.imageUrl} />
                            <div><Button buttonClick={() => removeFromCart(item)} type='secondary' size='small'>Remove</Button></div>

                        </div>
                    )}
                </div>
                {cartItems.length > 0 && (
                    <div className={styles.cart__actions}>
                        {cartItems.length === 2 && <div className={styles.cart__limitMsg}>You can borrow max 2 books at a time</div>}
                        <Button buttonClick={() => borrowBook()}>Borrow</Button>
                    </div>
                )}
            </div>
        </div>
    )
}

export default Cart
