import sqlite from 'sqlite';

export default async (req, res) => {
  const db = await sqlite.open('./library.sqlite');
  const userData = JSON.parse(req.body);
  // console.log(`userIduserIduserId----->${userData.userId}`);
  const userDetails = await db.get('SELECT * FROM Users where userId = ?', [userData.userId]);

  res.statusCode = 200;
  res.json({data: userDetails});
}
