import sqlite from 'sqlite';
import UserDetails from '../getUserDetails'

export default async (req, res) => {
  res.statusCode = 200;
  const db = await sqlite.open('./library.sqlite');
  const reqBody = JSON.parse(req.body);
  let bookIds = ''
  const userCurrentData = await UserDetails({body: { userId: reqBody.userId }})

  console.log(`userCurrentData---->${JSON.stringify(userCurrentData)}`);

  if (reqBody.cartData.length > 0) {
    for (let index = 0; index < reqBody.cartData.length; index++) {
        const statement = await db.prepare('UPDATE books SET availableQty= ? where bookId= ? ');
        const result = await statement.run(
            reqBody.cartData[index].availableQty-1,
            reqBody.cartData[index].bookId,
        )
        result.finalize();
        if (bookIds.length === 0)
            bookIds = reqBody.cartData[index].bookId.toString()
        else
            bookIds = bookIds.concat(`,${reqBody.cartData[index].bookId.toString()}`)
    }
  }

  console.log(`bookIdsbookIdsbookIds---->${bookIds}`);

  const userUpdateStatement = await db.prepare('UPDATE Users SET bookIds= ? where userId= ? ');
  const userUpdateResult = await userUpdateStatement.run(
      bookIds,
      reqBody.userId,
  )
  userUpdateResult.finalize();

  res.json({ 
    data: 'Borrowed succesfully'
  });
}
