import React from "react";
import Button from '../button'
import styles from './styles.module.scss';

const Cart = ({
    cartItems = [],
    removeFromCart,
}) => {
    return (
        <div className={styles.cart}>
            <div className={styles.cart__inner}>
                <div>
                    {cartItems.map(item => 
                        <div className={styles.cart__item}>
                            <img src={item.imageUrl} />
                            <div><Button buttonClick={() => removeFromCart(item)}>Remove</Button></div>

                        </div>
                    )}
                </div>
                <div className={styles.cart__actions}>
                    {cartItems.length === 2 && <div>You can borrow max 2 books at a time</div>}
                    <Button buttonClick={() => {}}>Borrow</Button>
                </div>
            </div>
        </div>
    )
}

export default Cart
