import React from "react";
import styles from './styles.module.scss';

const Button = ({
    children = 'Button text',
    type = 'primary',
    size = 'medium'
}) => {
    return (
        <Button className={`button ${type && styles.type} ${size && styles.size}`}>
            {children}
        </Button>
    )
}

export default Button
