import React, { Component } from 'react';
import styles from './styles.module.scss';
import BookComp from '../../components/book'
import NoResults from '../../components/noResults';

class ListingModule extends Component {
    constructor(props) {
        super(props);
        this.state = {
            bookListing: [
                {
                    bookId: 1,
                    availableQty: 3,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652378/bookImages/star.jpg',
                },
                {
                    bookId: 2,
                    availableQty: 4,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652381/bookImages/harry.jpg',
                },
                {
                    bookId: 3,
                    availableQty: 1,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652379/bookImages/sapiens.jpg',
                },
                {
                    bookId: 4,
                    availableQty: 2,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652380/bookImages/mind.jpg',
                },
                {
                    bookId: 5,
                    availableQty: 1,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652381/bookImages/sadh.jpg',
                },
                {
                    bookId: 6,
                    availableQty: 1,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652376/bookImages/georgeM.jpg',
                }
            ]
        }
    }

    componentDidMount() {

    }

    render() {
        const { bookListing } = this.state;
        return (
            <div className={styles.listing}>
                <div className={styles.listing__title}>
                    Listing page
                </div>
                <div className={styles.listing__subTitle}>
                    Select your desired book to borrow
                </div>
                <div className={styles.listing__bookListWrap}>
                    {bookListing > 0 ? 
                        bookListing.map(book => 
                            <div className={styles.listing__bookWrap}>
                                <BookComp bookDetails={book} />
                            </div>
                        )
                    : <NoResults textToShow="No books found" />
                    }
                </div>
            </div>
        );
    }
}

export default ListingModule;
