import React, { Component } from 'react';
import styles from './styles.module.scss';
import BookComp from '../../components/book'
import NoResults from '../../components/noResults';
import Cart from '../../components/Cart';

class ListingModule extends Component {
    constructor(props) {
        super(props);
        this.state = {
            cart: [],
            bookList: [
                {
                    bookId: 1,
                    availableQty: 3,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652378/bookImages/star.jpg',
                    isSelected: false,
                },
                {
                    bookId: 2,
                    availableQty: 4,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652381/bookImages/harry.jpg',
                    isSelected: false,
                },
                {
                    bookId: 3,
                    availableQty: 1,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652379/bookImages/sapiens.jpg',
                    isSelected: false,
                },
                {
                    bookId: 4,
                    availableQty: 2,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652380/bookImages/mind.jpg',
                    isSelected: false,
                },
                {
                    bookId: 5,
                    availableQty: 1,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652381/bookImages/sadh.jpg',
                    isSelected: false,
                },
                {
                    bookId: 6,
                    availableQty: 1,
                    bookName: 'BookName 1',
                    imageUrl: 'https://res.cloudinary.com/abyy30/image/upload/v1608652376/bookImages/georgeM.jpg',
                    isSelected: false,
                }
            ],
        }
    }

    componentDidMount() {

    }

    addToCart = bookDetails => {
        const { bookList, cart } = this.state;
        // case to be handle: already added book should not be added
        if (cart.length < 2 && bookDetails.availableQty > 0) {
            const updatedBookList = bookList.map(book => {
                if (book.bookId === bookDetails.bookId) {
                    book.isSelected = true
                    // book.availableQty =- 1
                }
                return book
            })
            this.setState({
                bookList: updatedBookList,
                cart: [...cart, bookDetails],
            })
        }
    };

    removeFromCart = bookDetails => {
        const { bookList, cart } = this.state;
        if (cart.length > 0) {
            const updatedBookList = bookList.map(book => {
                if (book.bookId === bookDetails.bookId)
                    book.isSelected = false
                return book
            })
            const updatedCart = cart.filter(item => item.bookId !== bookDetails.bookId)
            this.setState({
                bookList: updatedBookList,
                cart: updatedCart,
            })
        }
    };

    render() {
        const { bookList, cart } = this.state;
        return (
            <div className={styles.listing}>
                <Cart cartItems={cart} removeFromCart={this.removeFromCart} />
                <div className={styles.listing__title}>
                    Listing page
                </div>
                <div className={styles.listing__subTitle}>
                    Hover or Tap to select your desired book
                </div>
                <div className={styles.listing__bookListWrap}>
                    {bookList.length > 0 ? 
                        bookList.map(book => 
                            <div className={styles.listing__bookWrap} key={book.bookId}>
                                <BookComp bookDetails={book} addToCart={this.addToCart} removeFromCart={this.removeFromCart} />
                            </div>
                        )
                    : <NoResults textToShow='No books found' />
                    }
                </div>
            </div>
        );
    }
}

export default ListingModule;
