import sqlite from 'sqlite';

export default async (req, res) => {
  res.statusCode = 200;
  const db = await sqlite.open('./library.sqlite');
  const books1st = await db.all('select * from books');
  console.log(`bookList 1stttttttttt--->${JSON.stringify(books1st)}`);

  console.log(`req.body--->${req.body}`);

  if (req.body.length > 0) {
    for (let index = 0; index < req.body.length; index++) {
        const statement = await db.prepare('UPDATE books SET availableQty= ? where bookId= ? ');
        const result = await statement.run(
            req.body[index].availableQty-1,
            req.body[index].bookId,
        )
        console.log(`result---->${result}`);
        result.finalize();
    }
  }
//   const books = await db.all(`update books bookId = ${req.body.bookId}`);
  const books2nd = await db.all('select * from books');


  console.log(`req--->${req.body}`);
  console.log(`bookList 2nddddddd--->${JSON.stringify(books2nd)}`);
  res.json({ 
    data: 'Borrowed succesfully'
  });
}
