import React from "react";
import styles from './styles.module.scss';

const ButtonComp = ({
    children = 'Button text',
    type = 'primary',
    size = 'medium'
}) => {
    return (
        <button className={`${styles.button} ${styles.type} ${styles.size}`}>
            {children}
        </button>
    )
}

export default ButtonComp
