import sqlite from 'sqlite';
import UserDetails from '../getUserDetails'

export default async (req, res) => {
  res.statusCode = 200;
  const db = await sqlite.open('./library.sqlite');
  const reqBody = JSON.parse(req.body);
  let bookIds = ''
  const userCurrentData = await UserDetails({body: JSON.stringify({ userId: reqBody.userId })})

  const bookIdsBorrowed = userCurrentData.bookIds.split(',')
  const currentBooks = bookIdsBorrowed.filter(id => parseInt(id) !== reqBody.cartData.bookId) || [];

  if (currentBooks.length < 2 && reqBody.cartData.bookId) {

    const statement = await db.prepare('UPDATE books SET availableQty= ? where bookId= ? ');
    const result = await statement.run(
        reqBody.cartData.availableQty+1,
        reqBody.cartData.bookId,
    )
    result.finalize();

    if (bookIds.length === 0)
        bookIds = reqBody.cartData.bookId.toString()
    else
        bookIds = bookIds.concat(`,${reqBody.cartData.bookId.toString()}`)

    const userUpdateStatement = await db.prepare('UPDATE Users SET bookIds= ? where userId= ? ');
    const userUpdateResult = await userUpdateStatement.run(
        currentBooks[0],
        reqBody.userId,
    )
    userUpdateResult.finalize();

    res.json({ 
      data: 'Returned succesfully',
    });

  } else {
    res.json({ 
      data: 'Cannot be returned',
    });
  }


}
