import React from "react";
import Button from '../button'
import styles from './styles.module.scss';

const Slider = ({
    sliderItems = [],
    removeFromSlider,
    borrowBook,
    hideSlider
}) => {
    return (
        <div className={styles.cart}>
            <div className={styles.cart__title}>
                Cart
                <img src="https://res.cloudinary.com/abyy30/image/upload/v1608744290/cross_vho0yd.svg" onClick={hideSlider} />
            </div>
            <div className={styles.cart__inner}>
                <div>
                    {sliderItems.map(item => 
                        <div className={styles.cart__item} key={item.bookId}>
                            <img src={item.imageUrl} />
                            <div><Button buttonClick={() => removeFromSlider(item)} type='secondary' size='small'>Remove</Button></div>

                        </div>
                    )}
                </div>
                {sliderItems.length > 0 && (
                    <div className={styles.cart__actions}>
                        {sliderItems.length === 2 && <div className={styles.cart__limitMsg}>You can borrow max 2 books at a time</div>}
                        <Button buttonClick={() => borrowBook()}>Borrow</Button>
                    </div>
                )}
            </div>
        </div>
    )
}

export default Slider
